import React, { useState } from "react";

const GetSelections = () => {
    const LANGUAGE_SELECTIONS = ["EN", "TR", "AR", "PT", "RU"];
    const [languageSelected, setLanguageSelected] = useState(LANGUAGE_SELECTIONS[0]);
    const updateLanguageSelected = (event) => setLanguageSelected(event.target.value)

    const CATEGORY_SELECTIONS = ['business','entertainment','general','health','science','sports','technology']
    const [categorySelected, setCategorySelected] = useState(CATEGORY_SELECTIONS[0])
    const updateCategorySelected = (event) => setCategorySelected(event.target.value)

    return (
        <div className="getselections-container wrapper">
            <label>Language:</label>
            <select onChange={updateLanguageSelected}>
                {LANGUAGE_SELECTIONS.map((selection, index) => <option key={index} value={selection}>{selection}</option>)}
            </select>
            <h5>Language selected: {languageSelected}</h5>
            <label>Category:</label>
            <select onChange={updateLanguageSelected}>
                {CATEGORY_SELECTIONS.map((selection, index) => <option key={index} value={selection}>{selection}</option>)}
            </select>
            <h1>Category selected: {categorySelected}</h1>
        </div>
    );
};

export default GetSelections;
