import React, { useState } from "react";

const GetSelections = () => {
  const [languageSelected, setLanguageSelected] = useState("EN");
  const updateLanguageSelected = event => {
    setLanguageSelected(languageSelected => event.target.value);
  };

  return (
    <React.Fragment>
      <select onChange={updateLanguageSelected}>
        <option value="EN">EN</option>
        <option value="TR">EN</option>
      </select>
      <h1>Language Selected is:{languageSelected}</h1>
    </React.Fragment>
  );
};

export default GetSelections;
