import React, { useState } from "react";

const GetSelections = () => {
    const LANGUAGE_SELECTIONS = ['EN','TR','AR','pt','ru']
  const [languageSelected, setLanguageSelected] = useState(LANGUAGE_SELECTIONS[0]);
  const updateLanguageSelected = event => {
    setLanguageSelected(event.target.value);
  };

  return (
    <div className="getselections-container wrapper">
      <label>Language:</label>
      <select onChange={updateLanguageSelected}>
        <option value="EN">EN</option>
        <option value="TR">TR</option>
      </select>
      <h1>Language Selected is:{languageSelected}</h1>
    </div>
  );
};

export default GetSelections;
