import { useState } from "react";

const useValidation = () => {
  const [usernameToValidate, setUsernameToValidate] = useState();
  const [passwordToValidate, setPasswordToValidate] = useState();
  const [validationStatus, setValidationStatus] = useState([true]);
  const [validationResultsArray, setValidationResultsArray] = useState([]);
  const [errorArray, setErrorArray] = useState([]);

  const initiateValidation = (name, password) => {
    setUsernameToValidate(name);
    setPasswordToValidate(password);
    runValidationRules();
    return {};
  };

  const addToErrorArray = string => setErrorArray([...errorArray, string]);
  const addToValidationResultsArray = boolean =>
    setValidationResultsArray([...validationResultsArray, boolean]);

  const runValidationRules = () => {
    if (!usernameToValidate) {
        addToValidationResultsArray(false)
        addToErrorArray("Username does not exist!");
    }
    if (!passwordToValidate) {
        addToValidationResultsArray(false)
        addToErrorArray("Password does not exist!");
    } 
    if (passwordToValidate.length < 5 && passwordToValidate) {
        addToValidationResultsArray(false)
        addToErrorArray("Passoword length less than 5 characters!");
    }
    setValidationStatus(validationResultsArray.reduce((sum,num)=>sum || num))
  };

  return { validationStatus, errorArray, initiateValidation };
};

export default useValidation;
